mapper.select:
select
id, title, creator_id, status, detail, create_time, over_time, project_id
from union.wo


        and id = #{id}


        and title = #{title}


        and creator_id = #{creator_id}


        and status = #{status}


        and detail = #{detail}


        and create_time = #{create_time}


        and over_time = #{over_time}


        and project_id = #{project_id}
